import React, { useEffect, useState } from 'react'
import LoginButton from './components/LoginButton'
import LogoutButton from './components/LogoutButton'
import { useAuth0 } from '@auth0/auth0-react'
import { Listbox } from '@headlessui/react'

export default function App() {
  const {user} = useAuth0();
  const [organization, setOrganization] = useState();
  const [email, setEmail] = useState();
  const [organizations, setOrganizations] = useState();

  useEffect(() => {
    if (organizations) {
      setOrganization(organizations[0]);
    }
  }, [organizations]);

  const getOrgs = () => {
    fetch('http://localhost:8080/user-orgs-by-email?email=' + email)
    .then(response => response.json())
    .then(data => setOrganizations(data));
  }

  const handleSubmit = (evt) => {
    evt.preventDefault();
    getOrgs();
}

  return (
    <div style={{width:"200px", margin:"0 auto"}}>
      <h1>Team 0</h1>
      {!user && 
        <>
          <form onSubmit={handleSubmit}>
            <label>
              Email
              <input
                type="text"
                value={email}
                onChange={e => setEmail(e.target.value)}
              />
            </label>
            <input type="submit" value="Submit" />
          </form>
        </>
      }
      {organizations && organization &&
        <>
        <LoginButton organization={organization.id} email={email}/>
          <Listbox value={organization} onChange={setOrganization}>
            <Listbox.Button>{organization.display_name}</Listbox.Button>
            <Listbox.Options>
              {organizations.map((org) => (
                <Listbox.Option
                  key={org.id}
                  value={org}
                >
                  {org.display_name}
                </Listbox.Option>
              ))}
            </Listbox.Options>
          </Listbox>
        </>
      }
      {user &&
        <>
          <LogoutButton />
          <thead>
            <tr>
              <th>First Name</th>
              <th>Email</th>
              <th>Org</th>
            </tr>
          </thead>
          <tbody>
            <tr>
              <td>{user.nickname}</td>
              <td>{user.email}</td>
              {organization && 
                <td>{organization.id}</td>
              }
            </tr>
          </tbody>
        </>
      }
    </div>
  )
}
